import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as r,c as t,f as a}from"./app-4919810a.js";const s={},n=a('<h1 id="three-js" tabindex="-1"><a class="header-anchor" href="#three-js" aria-hidden="true">#</a> three.js</h1><h2 id="_3d-场景前置知识" tabindex="-1"><a class="header-anchor" href="#_3d-场景前置知识" aria-hidden="true">#</a> 3D 场景前置知识</h2><div class="hint-container info"><p class="hint-container-title">相关信息</p><p>1.场景(Scene)：是物体、光源等元素的容器，可以配合 chrome 插件使用，抛出 window.scene即可实时调整 obj 的信息和材质信息。<br> 2.相机（Camera）：场景中的相机，代替人眼去观察，场景中只能添加一个，一般常用的是透视相机（PerspectiveCamera）<br> 3.物体对象（Mesh）：包括二维物体（点、线、面）、三维物体，模型等等<br> 4.光源（Light）：场景中的光照，如果不添加光照场景将会是一片漆黑，包括全局光、平行光、点光源等<br> 5.渲染器（Renderer）:场景的渲染方式，如webGL\\canvas2D\\Css3D。<br> 6.控制器(Control): 可通过键盘、鼠标控制相机的移动</p></div>',3),c=[n];function i(o,h){return r(),t("div",null,c)}const l=e(s,[["render",i],["__file","threejs.html.vue"]]);export{l as default};
